AddModuleDescription('smiles.pl', 'Smilies');

# The smilies are from the Emacs 24 distribution. There, you'll find
# them in the etc/images/smilies/medium directory.

# Files: blink.xpm braindamaged.xpm cry.xpm dead.xpm evil.xpm forced.xpm
#        frown.xpm grin.xpm indifferent.xpm reverse-smile.xpm sad.xpm
#        smile.xpm wry.xpm
# Author: Adam SjÃ¸gren
# Copyright (C) 2007-2013 Free Software Foundation, Inc.
# License: GNU General Public License version 3 or later (see COPYING)

%Smilies = (
	    # blink
	    quotemeta(';-)') => 'data:image/png;base64,',
	    # braindamaged
	    quotemeta('8-)') => 'data:image/png;base64,',
	    # cry
	    quotemeta(':\'(') => 'data:image/png;base64,',
	    # dead
	    quotemeta('X-(') => 'data:image/png;base64,',
	    # evil
	    quotemeta('>-{') => 'data:image/png;base64,',
	    # forced
	    quotemeta(':-]') => 'data:image/png;base64,',
	    # frown
	    quotemeta('>-(') => 'data:image/png;base64,',
	    # grin
	    quotemeta(':-D') => 'data:image/png;base64,',
	    # indifferent
	    quotemeta(':-|') => 'data:image/png;base64,',
	    # reverse-smile
	    quotemeta('(-:') => 'data:image/png;base64,',
	    # sad
	    quotemeta(':-(') => 'data:image/png;base64,',
	    # smile
	    quotemeta(':-)') => 'data:image/png;base64,',
	    # wry
	    quotemeta(':-/') => 'data:image/png;base64,',
           );
